import React, {useState} from "react";
import Modale from "../components/Modale"
import ynov from "../images/pathway/ynov.jpeg";
import uppa from "../images/pathway/uppa.jpeg";
import harountazieff from "../images/pathway/harountazieff.jpeg";

const projets = [
    {
        image: ynov,
        title: 'Master',
        category: 'UI:UX'
    }
];

const Projets = (): JSX.Element => {
    const [showModal, setShowModal] = useState(false);

    const toggleModal = () => {
        setShowModal(!showModal);
    };

    return (
        <div>
            <section className="bg-white  lg:rounded-2xl dark:bg-[#111111]">
                <div className="container mb-8 px-4 sm:px-5 md:px-10 lg:px-[60px]">
                    <div className="py-12">
                        <h2 className="after-effect after:left-48 mt-12 lg:mt-0">Portfolio</h2>
                        {/* fillter group buttons */}
                        <ul className="button-group isotop-menu-wrapper mt-[30px] flex w-full justify-start md:justify-end flex-wrap font-medium">
                            <li className="fillter-btn mr-4 md:mx-4 is-checked" data-filter="*">All</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".dev">Video</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".plugin"> Web Design</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".branding"> Logo</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".custom">Graphic Design</li>
                        </ul>
                    </div>
                    {/* End py-12 */}

                    <div id="isotop-gallery-wrapper" className="mymix portfolio_list-two three-col">
                        <div className="grid-sizer"></div>
                        {/* portfolio items start */}
                        {projets.map((projet, index) => (
                            <div className="portfolio_list-two-items isotop-item dev branding ">
                                <button onClick={toggleModal} className="rounded-lg bg-[#fff3fc] dark:bg-transparent p-6 dark:border-[2px] border-[#212425]">
                                    <div className="overflow-hidden rounded-lg">
                                        <a href="#portfiloTwo" rel="modal:open">
                                            <img
                                                className="w-full cursor-pointer transition duration-200 ease-in-out transform hover:scale-110 rounded-lg h-auto"
                                                src={projet.image} alt="portfolio image"/>
                                        </a>
                                    </div>
                                    <span className="pt-5 text-[14px] font-normal text-gray-lite block dark:text-[#A6A6A6]">
                                        {projet.category}
                                    </span>
                                    <h2 className="font-medium cursor-pointer text-xl duration-300 transition hover:text-[#FA5252] dark:hover:text-[#FA5252] dark:text-white mt-2">
                                        <a href="#portfiloTwo" rel="modal:open">{projet.title}</a>
                                    </h2>
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
            <Modale isOpen={showModal} onClose={toggleModal} />
        </div>
    )
}

export default Projets;
