import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faPlateWheat, faList, faCalendarDay} from '@fortawesome/free-solid-svg-icons';


type ModaleProps = {
    isOpen: boolean;
    onClose: () => void;
    image: string;
    title: string;
    category: string;
    description: {
        title: string[];
        content: string[];
    };
    website: string;
    children?: React.ReactNode;
};

const ModaleProjets: React.FC<ModaleProps> = ({ isOpen, onClose, image,title, category,
                                                  description, website}) => {
    const [selectedDate, setSelectedDate] = useState<Date | null>(null);
    const [selectedList, setSelectedList] = useState("");

    const handleDateChange = (date: Date | null) => {
        if (date) {
            setSelectedDate(date);
            setSelectedList(""); // reset the value of selectedList
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed z-10 inset-0 overflow-y-auto">
            <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                <div
                    className="fixed inset-0 transition-opacity"
                    aria-hidden="true"
                    onClick={onClose}
                >
                    <div className={`absolute inset-0 bg-gray-300 opacity-80`}></div>
                </div>
                <span
                    className="hidden sm:inline-block sm:align-middle sm:h-screen"
                    aria-hidden="true"
                >

                </span>
                <div
                    className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl
                                transform transition-all my-8 align-middle max-w-[80vw]"
                    role="dialog"
                    aria-modal="true"
                    aria-labelledby="modal-headline"
                >
                    <div className="bg-white dark:bg-gray-800 p-10 sm:p-6 sm:pb-4">
                        <div className="overflow-y-scroll scroll-hide-1700 max-h-[80vh] dark:scrollbarDark scrollbarLight">
                            <h2 className="text-[#ef4060] dark:hover:text-[#FA5252] text-4xl text-center font-bold">
                                {title}
                            </h2>
                            <div className="grid grid-cols-1 lg:grid-cols-2 my-6 pr-3">
                                <div className="space-y-2">
                                    <p className="dark:text-white flex items-center text-[15px] sm:text-lg">
                                        <i className="fa-regular fa-file-lines sm:text-lg hidden sm:block mr-4 md:text-xl"></i>
                                        Projet :&nbsp;
                                        <span className="font-medium">{category}</span>
                                    </p>
                                </div>
                                <div className="space-y-2">
                                    <p className="dark:text-white flex items-center text-[15px] sm:text-lg">
                                        <i className="fa-solid fa-arrow-up-right-from-square text-lg mr-2 hidden sm:block"></i>
                                        Site web :&nbsp;
                                        <span className="font-medium transition-all duration-300 ease-in-out hover:text-[#ef4060]">
                                          <a href={website} target="_blank" rel="noopener noreferrer">{website}</a>
                                        </span>
                                    </p>
                                </div>
                            </div>

                            <p className="dark:text-white text-2line font-normal text-[15px] sm:text-sm">
                                {description.title.map((title, index) => (
                                    <div key={index}>
                                        <p className="dark:text-white flex items-center font-bold text-[15px] sm:text-lg">
                                            {title}
                                        </p>
                                        <p className="my-5">{description.content[index]}</p>
                                    </div>
                                ))}
                            </p>
                            <div className="pr-3">
                                <img className="w-full mt-6 rounded-lg h-auto"
                                     src={image} alt="portfolio image"/>
                            </div>
                        </div>
                        <button onClick={onClose} className="close bg-close-light dark:bg-close-dark">
                            Close
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ModaleProjets;
