import React, {useState} from "react";
import ModaleBlogs from "../components/ModaleBlogs"
import antigaspi from "../images/projets/AntiGaspi.png";

const blogs = [
    {
        color: '#fff0f0',
        image: antigaspi,
        title: 'Lutter contre le gaspillage alimentaire',
        category: '',
        description: 'Site web réalisé en binôme avec un autre étudiant de ma promotion. Notre objectif via la création de se site était de créer un site fullstack et notre thématique est : accélérer le troc et lutter ensemble et gratuitement contre le gaspillage alimentaire !',
        website: 'non hébergé'
    },
    {
        color: '#fff3fc',
        image: antigaspi,
        title: 'Test',
        category: 'testttt',
        description: '',
        website: ''
    }
];

const Blogs = (): JSX.Element => {
    const [showModal, setShowModal] = useState(false);
    const [selectedBlog, setSelectedBlog] = useState({ image: '', title: '', category: '', description: '', website: '' });

    const toggleModal = (blog: { image: string, title: string, category: string, description: string, website: string }) => {
        setSelectedBlog(blog);
        setShowModal(!showModal);
    };

    return (
        <div>
            <section className="container lg:rounded-2xl bg-white dark:bg-[#111111] px-4 sm:px-5 md:px-10 lg:px-20">
                <div className="container mb-8 px-4 sm:px-5 md:px-10 lg:px-[60px]">
                    <div className="py-12">
                        <h2 className="border-b-2 border-b-pink-600 text-[35px] dark:text-white font-medium pb-5 mb-5">
                            Mon blog
                        </h2>
                        {/* fillter group buttons */}
                        <ul className="button-group isotop-menu-wrapper mt-[30px] flex w-full justify-start md:justify-end flex-wrap font-medium">
                            <li className="fillter-btn mr-4 md:mx-4 is-checked" data-filter="*">All</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".dev">Video</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".plugin"> Web Design</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".branding"> Logo</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".custom">Graphic Design</li>
                        </ul>
                    </div>
                    {/* End py-12 */}

                    <div id="isotop-gallery-wrapper" className="mymix portfolio_list-two three-col">
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-x-6 gap-y-6">
                            {/* portfolio items start */}
                            {blogs.map((blog, index) => (
                                <div key={index} className={`py-4 dark:bg-transparent bg-[${blog.color}] pl-5 pr-3 space-y-2 mb-6 rounded-lg dark:border-[#212425] dark:border-2`}>
                                    <button onClick={() => toggleModal(blog)} className="w-full">
                                        <div className="overflow-hidden rounded-lg">
                                            <img className="object-cover w-full h-40 rounded-lg cursor-pointer transition duration-200 ease-in-out transform hover:scale-110"
                                                 src={blog.image} alt={`Image du projet : ${blog.title}`}/>
                                        </div>
                                        <span className="pt-5 text-[14px] font-normal text-gray-lite block dark:text-[#A6A6A6]">
                                        {blog.category}
                                    </span>
                                        <h2 className="font-medium cursor-pointer text-xl duration-300 transition hover:text-[#FA5252] dark:hover:text-[#FA5252] dark:text-white mt-2">
                                            {blog.title}
                                        </h2>
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                    <ModaleBlogs isOpen={showModal} onClose={() => setShowModal(false)}
                                   image={selectedBlog.image}
                                   title={selectedBlog.title}
                                   category={selectedBlog.category}
                                   description={selectedBlog.description}
                                   website={selectedBlog.website}
                    />
                </div>
            </section>
        </div>
    )
}

export default Blogs;
