import React, {useState} from "react";
import Modale from "../components/Modale"
import ynov from "../images/pathway/ynov.jpeg";

const projets = [
    {
        image: ynov,
        title: 'Master',
        category: 'UI:UX',
        description: '',
        languages: '',
        website: '',
        customer: ''
    },
    {
        image: ynov,
        title: 'Test',
        category: 'testttt',
        description: '',
        languages: '',
        website: '',
        customer: ''
    }
];

const Projets = (): JSX.Element => {
    const [showModal, setShowModal] = useState(false);
    const [selectedProjet, setSelectedProjet] = useState({ image: '', title: '', category: '',
                                                description: '', languages: '', website: '', customer: ''});

    const toggleModal = (projet: { image: string, title: string, category: string, description: string,
        languages: string, website: string, customer: string }) => {
        setSelectedProjet(projet);
        setShowModal(!showModal);
    };

    return (
        <div>
            <section className="container lg:rounded-2xl bg-white dark:bg-[#111111] px-4 sm:px-5 md:px-10 lg:px-20">
                <div className="container mb-8 px-4 sm:px-5 md:px-10 lg:px-[60px]">
                    <div className="py-12">
                        <h2 className="border-b-2 border-b-pink-600 text-[35px] dark:text-white font-medium pb-5 mb-5">Mes projets</h2>
                        {/* fillter group buttons */}
                        <ul className="button-group isotop-menu-wrapper mt-[30px] flex w-full justify-start md:justify-end flex-wrap font-medium">
                            <li className="fillter-btn mr-4 md:mx-4 is-checked" data-filter="*">All</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".dev">Video</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".plugin"> Web Design</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".branding"> Logo</li>
                            <li className="fillter-btn mr-4 md:mx-4" data-filter=".custom">Graphic Design</li>
                        </ul>
                    </div>
                    {/* End py-12 */}

                    <div id="isotop-gallery-wrapper" className="mymix portfolio_list-two three-col">
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-x-6 gap-y-6">
                        {/* portfolio items start */}
                        {projets.map((projet, index) => (
                            <div key={index} className="portfolio_list-two-items isotop-item dev branding ">
                                <button onClick={() => toggleModal(projet)} className="rounded-lg bg-[#fff3fc] dark:bg-transparent p-6 dark:border-[2px] border-[#212425]">
                                    <div className="overflow-hidden rounded-lg">
                                        <a href="#portfiloTwo" rel="modal:open">
                                            <img
                                                className="w-full cursor-pointer transition duration-200 ease-in-out transform hover:scale-110 rounded-lg h-auto"
                                                src={projet.image} alt="portfolio image"/>
                                        </a>
                                    </div>
                                    <span className="pt-5 text-[14px] font-normal text-gray-lite block dark:text-[#A6A6A6]">
                                        {projet.category}
                                    </span>
                                    <h2 className="font-medium cursor-pointer text-xl duration-300 transition hover:text-[#FA5252] dark:hover:text-[#FA5252] dark:text-white mt-2">
                                        <a href="#portfiloTwo" rel="modal:open">{projet.title}</a>
                                    </h2>
                                </button>
                            </div>
                        ))}
                        </div>
                    </div>
                    <Modale isOpen={showModal} onClose={() => setShowModal(false)}
                            image={selectedProjet.image}
                            title={selectedProjet.title}
                            category={selectedProjet.category}
                            description={selectedProjet.description}
                            languages={selectedProjet.languages}
                            website={selectedProjet.website}
                            customer={selectedProjet.customer}
                    />
                </div>
            </section>
        </div>
    )
}

export default Projets;
